import React, { Component, useState } from "react";
import Dropzone from "react-dropzone";
import './professor-marvin.css';
import UploadService from "./services/upload-files.service";

export default class ProfessorMarvin extends Component {
    constructor(props) {
        super(props);    
        this.state = {
          selectedFiles: undefined,
          currentFile: undefined,
          progress: 0,
          delay: 0,
          fileInfos: [],
          user_message: 'Type a message...'
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }

    delay = ms => new Promise(res => setTimeout(res, ms));

    onDrop = (files) => {
        if (files.length > 0) {
          this.setState({ selectedFiles: files });
        }
    }
    componentDidMount = () => {
        UploadService.getFiles().then((files) => {
          this.setState({
            fileInfos: files.data['data'],
          });
        });
    }

    upload = async () => {
        let currentFile = this.state.selectedFiles[0];

        this.setState({
          progress: 0,
          currentFile: currentFile,
        });

        await UploadService.upload(currentFile)
          .then((response) => {
            this.setState({
              message: response.message,
            });

            if (response.already_exists) {
              this.setState({
                delay: 0,
                progress: 0
              });
            }
            else {
              this.setState({
                delay: 500,
                progress: 100
              });
            }

            return UploadService.getFiles();
          })
          .then((files) => {
            this.setState({
              fileInfos: files.data['data'],
            });

          })
          .catch(() => {
            console.log('failed');
            this.setState({
              progress: 0,
              delay: 0,
              message: "ERROR: File is not a PDF",
              currentFile: undefined,
            });
          });

        await this.delay(this.state.delay);
        this.setState({
          selectedFiles: undefined,
          progress: 0,
        });

    }

    removeFile = async (filename) => {     
      await UploadService.removeFile(filename)
        .then((response) => {
          this.setState({
            message: response.message,
          });
          return UploadService.getFiles();
        })
        .then((files) => {
          this.setState({
            fileInfos: files.data['data'],
          });
        })
        .catch(() => {
          this.setState({
            message: "ERROR: File unable to be deleted",
          });
        });
    }

    deleteAll = async () => {
      await UploadService.resetApp()
        .then((response) => {
          this.setState({
            message: response.message,
          });
          return UploadService.getFiles();
        })
        .then((files) => {
          this.setState({
            fileInfos: files.data['data'],
          });
        })
        .catch(() => {
          this.setState({
            message: "ERROR: Unable to delete all files",
          });
        });
    }

    handleChange(event) {
      this.setState({user_message: event.target.value});
    }

    handleSubmit(event) {
      alert('An essay was submitted: ' + this.state.user_message);
      event.preventDefault();
    }



    render() {
        const { selectedFiles, currentFile, progress, message, fileInfos } = this.state;

        return (
          <div>

            <aside>
              <div className="card message-box">
                <table className="table-border">
                  <thead className="header">
                    <tr>&nbsp;&nbsp;&nbsp;Professor Marvin</tr>
                  </thead>
                  <tbody className="body">
                    <tr className="marvin-messages"><td>1</td></tr>
                    <tr className="user-messages"><td>2</td></tr>
                    <tr className="marvin-messages"><td>4</td></tr>
                    <tr className="marvin-messages"><td>5</td></tr>
                    <tr className="marvin-messages"><td>6</td></tr>
                    <tr className="marvin-messages"><td>7</td></tr>
                    <tr className="marvin-messages"><td>8</td></tr>
                    <tr className="marvin-messages"><td>9</td></tr>
                    <tr className="marvin-messages"><td>10</td></tr>
                    <tr className="marvin-messages"><td>11</td></tr>
                    <tr className="marvin-messages"><td>12</td></tr>
                    <tr className="marvin-messages"><td>13</td></tr>
                    <tr className="marvin-messages"><td>14</td></tr>
                    <tr className="marvin-messages"><td>15</td></tr>
                    <tr className="marvin-messages"><td>16</td></tr>
                    <tr className="marvin-messages"><td>17</td></tr>
                    <tr className="marvin-messages"><td>18</td></tr>
                    <tr className="marvin-messages"><td>19</td></tr>
                    <tr className="marvin-messages"><td>20</td></tr>
                    <tr className="marvin-messages"><td>21</td></tr>
                    <tr className="marvin-messages"><td>22</td></tr>
                  </tbody>
                </table>
              </div>
            </aside>

            <aside>
              <div className="messages-input-div">
                <input onChange={this.handleChange} className="messages-input-field" type="text"/>‚Äç
                <button onClick={this.handleSubmit} className="messages-input-button">Send!</button>
              </div>
            </aside>


            <aside>
              <br></br>
              <a href="/upload_page">
                <button 
                  className="btn btn-go-to-upload">
                  &lt; Edit Files
                </button>
              </a>
            </aside>
          </div>
        );
    }    
}
