import { configure } from "@testing-library/react";
import http from "../http-common";
import testfile from "./../../pdf_uploads/arjav@umich.edu-r.pdf"

class UploadFilesService {
  upload = async (file) => {
    let formData = new FormData();

    formData.append('File', file);

    let response = await fetch('/upload',
      {
        method: 'post',
        body: formData,
      },
    );
    let res = await response.json();

    console.log(res.filename);

    const OtherComponent = React.lazy(() => import('./../../pdf_uploads/arjav@umich.edu-r.pdf'));

    console.log(testfile);
    console.log(OtherComponent);


    return res;
  }

  removeFile = async (filename) => {
    let formData = new FormData();
    formData.append('filename', filename);

    let response = await fetch('/remove_file',
      {
        method: 'post',
        body: formData,
      },
    );
    let res = await response.json();
    return res;
  }

  resetApp = async () => {
    let response = await fetch('/reset',
      {
        method: 'get',
      },
    );
    let res = await response.json();
    return res;
  }


  getFiles() {
    return http.get("/files");
  }
}

export default new UploadFilesService();
