import React, { Component } from "react";
import Dropzone from "react-dropzone";
import './upload-files.css';
import UploadService from "./services/upload-files.service";

export default class UploadFiles extends Component {
    constructor(props) {
        super(props);    
        this.state = {
          selectedFiles: undefined,
          currentFile: undefined,
          progress: 0,
          message: "",

          fileInfos: [],
        };

    }

    onDrop = (files) => {
        if (files.length > 0) {
          this.setState({ selectedFiles: files });
        }
    }

    componentDidMount = () => {
        UploadService.getFiles().then((response) => {
          this.setState({
            fileInfos: [],
          });
        });

        window.addEventListener("load",function() {
          document.getElementById("employeeLink").addEventListener("click",function(e) {
            e.preventDefault(); // cancel the link
            document.getElementById("myForm").submit(); // but make sure nothing has name or ID="submit"
          });
        });
    }

    upload = async () => {
        let currentFile = this.state.selectedFiles[0];

        this.setState({
          progress: 0,
          currentFile: currentFile,
        });

        //console.log(currentFile);
        await UploadService.upload(currentFile)
          .then((response) => {
            this.setState({
                progress: 100
            });
            this.setState({
              message: response.message,
            });
            return UploadService.getFiles();
          })
          .then((files) => {
            this.setState({
              fileInfos: files.data['data'],
            });
          })
          .catch(() => {
            console.log('failed');
            this.setState({
              progress: 0,
              message: "ERROR: File is not a PDF",
              currentFile: undefined,
            });
          });

        this.setState({
          selectedFiles: undefined,
        });
    }

    render() {
        const { selectedFiles, currentFile, progress, message, fileInfos } = this.state;

        return (
          <div>
            {currentFile && (
              <div className="progress mb-3">
                <div
                  className="progress-bar progress-bar-info progress-bar-striped"
                  role="progressbar"
                  aria-valuenow={progress}
                  aria-valuemin="0"
                  aria-valuemax="100"
                  style={{ width: progress + "%" }}
                >
                  {progress}%
                </div>
              </div>
            )}

            <Dropzone onDrop={this.onDrop} multiple={false}>
              {({ getRootProps, getInputProps }) => (
                <section>
                  <div {...getRootProps({ className: "dropzone" })}>
                    <input {...getInputProps()} />
                    {selectedFiles && selectedFiles[0].name ? (
                      <div className="selected-file">
                        {selectedFiles && selectedFiles[0].name}
                      </div>
                    ) : (
                      "Drag and drop file here, or click to select file"
                    )}
                  </div>
                  <aside className="selected-file-wrapper">
                    <button
                      className="btn btn-success"
                      disabled={!selectedFiles}
                      onClick={this.upload}>
                      Upload
                    </button>
                  </aside>
                </section>
              )}
            </Dropzone>

            <div className="alert alert-light" role="alert">
              {message}
            </div>

            {fileInfos.length > 0 && (
              <div className="card">
                <div className="card-header">List of Files</div>
                <ul className="list-group list-group-flush">
                  {fileInfos.map((currFile) => (
                    <li className="list-group-item" key={currFile.index}>
                      <div>
                        <a class="card-text-big" href={currFile.file.url}>{currFile.file.name}</a>
                        <a id='form' class="card-text-small" href='/remove-file/{currFile.file.name}'>&nbsp;&nbsp; &nbsp; (Remove)</a>
                        <form action="/remove-file/" method="post" id="signup" enctype="multipart/form-data">
                          <input type="hidden" name="file_name" value={currFile.file.name}/>
                          <button class="card-text-small" type="submit">Remove</button>
                        </form>
                      </div>
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        );
    }    
}
