import React, { Component } from "react";
import './home.css';
import marvin_logo from './images/marvin_logo.png'

export default class Home extends Component{
  componentDidMount() {
    let el = document.querySelector('.home');
    el.classList.add('home');
    el.classList.add('fade-in');
  }

  render() {
    return (
      <div className="home">
        <div className="container">
          <div className="my-5 check">
            <p className="title-text">Welcome to Professor Marvin - Your Simple and Effective Study Tool!</p>
            <img className="logo" src={marvin_logo} alt="Marvin Logo"></img>
            <br></br>
            <p className="sub-text">Simply upload some files and talk to Professor Marvin as if he is your personal tutor</p>
            <br></br>
            <form className="center" action="/upload_page">
              <button className="btn btn-go-to-upload-from-start" type="submit">Click here to get started!</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}
